mapper.select:
and activity_name like concat('%', #{activityName}, '%')
and ( (start_time >= #{startTime} and start_time <= #{endTime}) OR (end_time >= #{startTime} and end_time <= #{endTime}) OR (start_time <= #{startTime} and end_time >= #{endTime}) )
and start_time >= #{startTime} and start_time < DATE_ADD(#{startTime}, INTERVAL 1 DAY)
and end_time <= #{endTime}
and activity_location like concat('%', #{activityLocation}, '%')
and organizer like concat('%', #{organizer}, '%')
and activity_type = #{activityType}
and status = #{status}
ORDER BY CASE status WHEN 'Êú™ÂºÄÂßã' THEN 1 WHEN 'ÂèØÊä•Âêç' THEN 2 WHEN 'Â∑≤Êà™Ê≠¢' THEN 3 WHEN 'ËøõË°å‰∏≠' THEN 4 WHEN 'Â∑≤ÁªìÊùü' THEN 5 ELSE 6 END, created_at DESC

mapper.update:
update activities
activity_name = #{activityName}, start_time = #{startTime}, end_time = #{endTime}, activity_location = #{activityLocation}, activity_capacity = #{activityCapacity}, activity_start = #{activityStart}, activity_deadline = #{activityDeadline}, activity_description = #{activityDescription}, organizer = #{organizer}, notes = #{notes}, activity_type = #{activityType}, status = #{status}, picture_url = #{pictureUrl}, activity_total_capacity = #{activityTotalCapacity},
where activity_id = #{activityId}

UPDATE activities
SET activity_capacity = activity_capacity + 1,
    version = version +1
WHERE activity_id = #{activityId}
  and version = #{version}
  AND activity_capacity < activity_total_capacity

UPDATE activities
SET status = #{status}
WHERE activity_id = #{activityId}

mapper.select:
SELECT COUNT(1)
FROM activities
WHERE activity_name = #{activityName}
AND organizer = #{organizer}

    AND activity_id != #{activityId}
