mapper.select:
SELECT booking_id AS bookingId, student_id AS studentId, activity_id AS activityId, booked_at AS bookAt, create_by, create_time, update_by, update_time, remark FROM bookings
AND student_id = #{studentId}
AND activity_id = #{activityId}
ORDER BY booked_at DESC

mapper.delete:
DELETE FROM bookings
WHERE booking_id IN

    #{id}
