mapper.select:


     and stu_info.student_id = #{studentId}
     and stu_info.student_name = #{studentName}
     and activity_name like concat('%', #{activityName}, '%')
     and activity_level = #{activityLevel}
     and award_level = #{awardLevel}
     and scholarship_points = #{scholarshipPoints}
     and award_date = #{awardDate}
     and proof_material = #{proofMaterial}
     and semester = #{semester}
     and apply_time = #{applyTime}
     and nick_name like concat('%', #{nickName}, '%')
     and audit_status = #{auditStatus}
     and audit_time = #{auditTime}
     and audit_remark = #{auditRemark}

        AND award_date >= #{awardDateBegin}


        AND award_date <= #{awardDateEnd}


ORDER BY
CASE audit_status
WHEN '未提交' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未审核排最前
WHEN '未审核' THEN 2    -- 未通过其次
WHEN '已通过' THEN 3    -- 已通过最后
ELSE 4                 -- 其他状态（如果有）
END,
apply_time DESC            -- 按提交时间倒序



    and audit_status != '未提交'
     and student_id = #{studentId}
     and activity_name like concat('%', #{activityName}, '%')
     and activity_level = #{activityLevel}
     and award_level = #{awardLevel}
     and scholarship_points = #{scholarshipPoints}
     and award_date = #{awardDate}
     and proof_material = #{proofMaterial}
     and semester = #{semester}
     and apply_time = #{applyTime}
     and nick_name like concat('%', #{nickName}, '%')
     and audit_status = #{auditStatus}
     and audit_time = #{auditTime}
     and audit_remark = #{auditRemark}

        AND award_date >= #{awardDateBegin}


        AND award_date <= #{awardDateEnd}


ORDER BY
CASE audit_status
WHEN '未审核' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未通过其次
WHEN '已通过' THEN 2    -- 已通过最后
ELSE 3                 -- 其他状态（如果有）
END,
apply_time DESC            -- 按提交时间倒序
