mapper.select:


     and stu_info.student_id = #{studentId}
     and stu_info.student_name = #{studentName}
     and stu_info.academy = #{academy} and competition_name like concat('%', #{competitionName}, '%')
     and competition_level = #{competitionLevel}
     and award_level = #{awardLevel}
     and scholarship_points = #{scholarshipPoints}
     and award_date = #{awardDate}
     and proof_material = #{proofMaterial}
     and audit_status = #{auditStatus}
     and apply_time = #{applyTime}
     and nick_name like concat('%', #{nickName}, '%')
     and audit_time = #{auditTime}
     and audit_remark = #{auditRemark}
     and semester = #{semester}
     and stu_info.student_name like concat('%', #{studentName}, '%')

ORDER BY
CASE audit_status
WHEN '未提交' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未审核排最前
WHEN '未审核' THEN 2    -- 未通过其次
WHEN '已通过' THEN 3    -- 已通过最后
ELSE 4                 -- 其他状态（如果有）
END,
award_date ASC            -- 按提交时间倒序

mapper.update:
update stu_competition_record
student_id = #{studentId}, competition_name = #{competitionName}, competition_level = #{competitionLevel}, award_level = #{awardLevel}, scholarship_points = #{scholarshipPoints}, award_date = #{awardDate}, proof_material = #{proofMaterial}, audit_status = #{auditStatus}, apply_time = #{applyTime}, nick_name = #{nickName}, audit_time = #{auditTime}, audit_remark = #{auditRemark}, semester = #{semester}, student_name = #{studentName},
where competition_id = #{competitionId}

mapper.select:


    and audit_status != '未提交'
     and stu_info.student_id = #{studentId}
     and stu_info.student_name = #{studentName}
     and stu_info.academy = #{academy}
     and competition_name like concat('%', #{competitionName}, '%')
     and competition_level = #{competitionLevel}
     and award_level = #{awardLevel}
     and scholarship_points = #{scholarshipPoints}
     and award_date = #{awardDate}
     and proof_material = #{proofMaterial}
     and semester = #{semester}
     and apply_time = #{applyTime}
     and nick_name like concat('%', #{nickName}, '%')
     and audit_status = #{auditStatus}
     and audit_time = #{auditTime}
     and audit_remark = #{auditRemark}

        AND award_date >= #{awardDateBegin}


        AND award_date <= #{awardDateEnd}


ORDER BY
CASE audit_status
WHEN '未审核' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未通过其次
WHEN '已通过' THEN 2    -- 已通过最后
ELSE 3                 -- 其他状态（如果有）
END,
apply_time ASC            -- 按提交时间倒序

SELECT COUNT(1)
FROM stu_competition_record
WHERE student_id = #{studentId}
  AND competition_name = #{competitionName}
  AND competition_level = #{competitionLevel}
  AND award_level = #{awardLevel}
  AND semester = #{semester} LIMIT 1
